// CollectdTemplate is a template for a nginx collectd config file
var CollectdTemplate = template.Must(collectd.InjectTemplateFuncs(template.New("nginx")).Parse(`
<LoadPlugin "nginx">
  Interval {{.IntervalSeconds}}
</LoadPlugin>
<Plugin "nginx">
  <Instance "{{.Name}}[monitorID={{.MonitorID}}]">
    {{with .URL}}
    URL "{{renderValue . $ }}"
    {{end}}
    Timeout {{withDefault .Timeout 5000}}
    {{with .Username -}}
    User "{{.}}"
    {{- end}}
    {{with .Password -}}
    Password "{{.}}"
    {{- end}}
  </Instance>
</Plugin>
`)).Option("missingkey=error")
